mapper.select:
/** selectCmpInfoList- 회사 제안 정보 목록 조회 **/ SELECT @rownum:=@rownum+1 AS rownum , V.* FROM (
SELECT A.CMP_NO AS cmpNo , A.CMP_TITLE AS cmpTitle , A.CMP_LOCATION AS cmpLocation , A.CMP_FOUNDED_YEAR AS cmpFoundedYear , DATE_FORMAT(sysdate(), '%Y') - A.CMP_FOUNDED_YEAR AS foundedYear , FORMAT(A.CMP_EMP_CNT, 0) AS cmpEmpCnt , FORMAT(A.CMP_TOUCH, 0) AS cmpTouch , A.CMP_KIND AS cmpKind , A.CMP_DEADLINE_DTTM AS cmpDeadlineDttm , DATEDIFF(A.CMP_DEADLINE_DTTM, DATE_FORMAT(sysdate(), '%Y-%m-%d')) AS deadlineDay , A.CMP_JOB_POSITION AS cmpJobPosition , A.CMP_JOB_RESPONSE AS cmpJobResponse , A.CMP_JOB_QUALIFICATION AS cmpJobQualification , A.CMP_JOB_PREFER AS cmpJobPrefer , A.CMP_WAREFARE AS cmpWareFare , A.CMP_COMMENT AS cmpComment , DATE_FORMAT(A.REG_DTTM, '%Y-%m-%d') AS regDttm , CASE WHEN DAYOFWEEK(A.REG_DTTM) = 1 THEN '일' WHEN DAYOFWEEK(A.REG_DTTM) = 2 THEN '월' WHEN DAYOFWEEK(A.REG_DTTM) = 3 THEN '화' WHEN DAYOFWEEK(A.REG_DTTM) = 4 THEN '수' WHEN DAYOFWEEK(A.REG_DTTM) = 5 THEN '목' WHEN DAYOFWEEK(A.REG_DTTM) = 6 THEN '금' WHEN DAYOFWEEK(A.REG_DTTM) = 7 THEN '토'END AS regDayOfWeek , A.MOD_DTTM AS modeDttm , A.USE_YN AS useYn , A.CMP_APPLY_YN AS cmpApplyYn , A.CMP_INCEDENCE_RATE AS cmpIncedenceRate , A.CMP_RESIGNATION_RATE AS cmpResignationRate , A.CMP_RECURIT_URL AS cmpRecuritUrl , A.CMP_HOMEPAGE_URL AS cmpHomepageUrl , A.CMP_PROGRAMMER_KIND AS cmpProgrammerKind , A.CMP_FAVORITES_YN AS cmpFavoritesYn , A.CMP_GOOD_YN AS cmpGoodYn , A.CMP_RECURIT_KIND AS cmpRecuritKind , A.CMP_RECURIT_PROCESS AS cmpRecuritProcess , A.CMP_REPLY_YN AS cmpReplyYn , A.CMP_BLOCK_YN AS cmpBlockYn FROM TB_CMP_INFO A WHERE 1=1 AND A.CMP_OFFER_YN = 'Y'
AND A.CMP_TITLE LIKE CONCAT('%', #{cmpTitle}, '%')
AND A.CMP_LOCATION LIKE CONCAT('%', #{cmpLocation}, '%')
AND A.CMP_RECURIT_KIND = #{cmpRecuritKind}
AND A.CMP_APPLY_YN = #{cmpApplyYn}
AND DATE_FORMAT(A.REG_DTTM, '%Y-%m-%d') BETWEEN #{regDttm} AND #{regDttmEnd}
ORDER BY USE_YN DESC, A.CMP_FAVORITES_YN DESC, A.REG_DTTM DESC
) V, (select @rownum:=0) R

		/** cmpOfferInfoListCnt- 회사 제안 정보 목록 카운트 **/
		SELECT	COUNT(*)	AS cmpOfferInfoCnt
		FROM 	TB_CMP_INFO A
		WHERE	1=1
		AND		A.CMP_OFFER_YN = 'Y'

        AND 	A.CMP_TITLE LIKE CONCAT('%', #{cmpTitle}, '%') 


        AND 	A.CMP_LOCATION LIKE CONCAT('%', #{cmpLocation}, '%') 


        AND 	A.CMP_RECURIT_KIND = #{cmpRecuritKind} 


        AND 	A.CMP_APPLY_YN = #{cmpApplyYn}


        AND 	DATE_FORMAT(A.REG_DTTM, '%Y-%m-%d') BETWEEN #{regDttm} AND #{regDttmEnd}

         ORDER BY USE_YN DESC, A.CMP_FAVORITES_YN DESC, A.REG_DTTM DESC

mapper.insert:
    	/** insertCmpOfferInfo-  회사_제안 회사 등록 **/
    	INSERT INTO TB_CMP_INFO(
				CMP_NO
			,	CMP_TITLE
		    ,	CMP_RECURIT_KIND
		    ,	CMP_COMMENT
		    ,	USE_YN
		    ,	CMP_APPLY_YN			
		    ,	CMP_OFFER_YN
		    ,	REG_DTTM


		    ,	CMP_LOCATION
	   		, 	CMP_FOUNDED_YEAR
		    ,	CMP_EMP_CNT
		    ,	CMP_TOUCH
		    ,	CMP_KIND
		    ,	CMP_INCEDENCE_RATE	
		    ,	CMP_RESIGNATION_RATE


		   	,	CMP_BLOCK_YN	


		)
		SELECT 
				COUNT(cmp_no)+1 
			,	#{cmpTitle}
		    ,	#{cmpRecuritKind}
		    ,	#{cmpComment}
			,	'Y'
		    ,	'N'
		    ,	'Y'



			,	sysdate()


			,	CONCAT(DATE_FORMAT(#{regDttm}, '%Y%m%d'), DATE_FORMAT(SYSDATE(), '%H%i%S'))	





		    ,	#{cmpLocation}
	   		,	#{cmpFoundedYear}
		    ,	#{cmpEmpCnt}
		    ,	#{cmpTouch}
		    ,	#{cmpKind}
		    ,	#{cmpIncedenceRate}
		    ,	#{cmpResignationRate}


		    ,	"Y"


		FROM 	TB_CMP_INFO
